import { useState, useEffect } from "react";
import axios from 'axios';

function Books(props) {
    const [bookTitle, setBookTitle] = useState('');
    const [year, setYear] = useState(0);
    const [url, setURL] = useState('');
    const [bookInfo, setBookInfo] = useState([]);
    const [used, setUsed] = useState(false);
    const [err, setErr] = useState('')

    useEffect(async function() {
        try {
            var data = await axios('https://www.googleapis.com/books/v1/volumes', {params: {q: props.query, key: 'AIzaSyD9AyhB8F--bj5fLXUp5fMnGxDy0VXLAt0'}});
            setBookInfo(data.data.items);
            setUsed(true);
            console.log(data.data.items)
        } catch(e) {
            setErr("No Books Available")
        }
    }, [])
    if (used) {
        return (
            <div>
                <h2>Related Books:</h2>
                    {
                        bookInfo.map(function(i, index) {
                            return (
                                <div className="row">
                                    <div className="col-2">
                                        <img src="http://books.google.com/books/content?id=8bJQPgAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api"/>
                                    </div>
                                    <div className="col align-content-left" >
                                        <h3>{i.volumeInfo.title} by {i.volumeInfo.authors[0]} </h3>
                                    </div>
                                </div>
                            )
                        })
                    }
            </div>
        )
    } else {
        return (
            <div>{err}</div>
        )
    }
}

export default Books;
