import { useState } from "react";

function LightSwitch() {
    const [lightSwitch, setLightSwitch] = useState(false);

    function handleTurnOn(e) {
        e.preventDefault();
        setLightSwitch(true);
    }

    function handleTurnOff(e) {
        e.preventDefault();
        setLightSwitch(false);
    }

    return (
        <div>
            <p>{lightSwitch ? "The light switch is on": "The light switch is off"}</p>
            <button onClick={handleTurnOn} disabled={lightSwitch}>Turn On</button>
            <button onClick={handleTurnOff} disabled={!lightSwitch}>Turn Off</button>
        </div>
    )
}

export default LightSwitch;
