import { useState, useEffect } from "react";
import axios from "axios";
import Highcharts from 'highcharts/highcharts'
import PieChart from "highcharts-react-official"

function PieVis() {
    const [sales, setSales] = useState({});

    const config = {
        chart: {
            plotBackgroundColor: null,
            plotBorderWidth: null,
            plotShadow: false,
            type: 'pie'
        },
        title: {
            text: 'Browser market shares in January, 2018'
        },
        tooltip: {
            pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'
        },
        accessibility: {
            point: {
                valueSuffix: '%'
            }
        },
        plotOptions: {
            pie: {
                allowPointSelect: true,
                cursor: 'pointer',
                dataLabels: {
                    enabled: true,
                    format: '<b>{point.name}</b>: {point.percentage:.1f} %'
                }
            }
        },
        series: [{
            name: 'Brands',
            colorByPoint: true,
            data: sales
        }]
    }


    useEffect(async function () {
        var data = await axios.get("http://localhost:8080/earnings")
        console.log(data)
        console.log(data.data)
        var formattedData = data.data.map(function(earnings) {
            return {
                name: earnings.country,
                number: earnings.earnings
                }
            }
        )
        console.log(formattedData)
    }, [])

    return (
        <div>
            <PieChart highcharts={Highcharts} options={config}/>
        </div>
    )
}

export default PieVis;
