import { useState, useEffect } from "react";
import axios from "axios";
import Highcharts from 'highcharts/highcharts';
import BarChart from 'highcharts-react-official';

function BarVis() {
    const [earnings, setEarnings] = useState([]);
    const [sales,setSales] = useState([]);
    const [salesFigs,setSalesFigs] = useState([]);
    const [salesYear,setSalesYear] = useState([]);
    const config = {
        chart: {
            type: 'column'
        },
        title: {
            text: 'Monthly Average Rainfall'
        },
        subtitle: {
            text: 'Source: WorldClimate.com'
        },
        xAxis: {
            categories: salesYear[0],
            crosshair: true
        },
        yAxis: {
            min: 0,
            title: {
                text: 'Rainfall (mm)'
            }
        },
        tooltip: {
            headerFormat: '<span style="font-size:10px">{point.key}</span><table>',
            pointFormat: '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
                '<td style="padding:0"><b>{point.y:.1f} mm</b></td></tr>',
            footerFormat: '</table>',
            shared: true,
            useHTML: true
        },
        plotOptions: {
            column: {
                pointPadding: 0.2,
                borderWidth: 0
            }
        },
        series: [{
            name: 'Tokyo',
            data: [49.9, 71.5, 106.4, 129.2, 144.0, 176.0, 135.6, 148.5, 216.4, 194.1, 95.6, 54.4]

        }, {
            name: 'New York',
            data: [83.6, 78.8, 98.5, 93.4, 106.0, 84.5, 105.0, 104.3, 91.2, 83.5, 106.6, 92.3]

        }, {
            name: 'London',
            data: [48.9, 38.8, 39.3, 41.4, 47.0, 48.3, 59.0, 59.6, 52.4, 65.2, 59.3, 51.2]

        }, {
            name: 'Berlin',
            data: [42.4, 33.2, 34.5, 39.7, 52.6, 75.5, 57.4, 60.4, 47.6, 39.1, 46.8, 51.1]

        }]
    }

    useEffect(async function() {
        var dateSet = false
        var data = await axios.get("http://localhost:8080/sales")
        console.log(data.data);
        var formattedSales = data.data.map(function(earnings) {
            return earnings.country
        })
        var formattedSalesFigs = data.data.map(function(earnings) {
            return earnings.data
        }, [])
        var formattedSalesYear = data.data.map(function(earnings) {
            return earnings.years
        }, [])
        setSales(formattedSales)
        setSalesFigs(formattedSalesFigs)
        for (let i=1; i<formattedSalesYear.length; i++) {
            if (formattedSalesYear[i] === formattedSalesYear[i-1]) {
                dateSet = true
            } else {
                dateSet = false
                break
            }
        }
        if (dateSet) {
            setSalesYear(formattedSalesYear)
        }
    }, [])

    return (
        <div>
            <BarChart highcharts={Highcharts} options={config}/>
        </div>
    )
}

export default BarVis;
