import { useState } from "react";
import axios from "axios";

function Weather() {
    const [query, setQuery] = useState('');

    function handleSearchQuery(e) {
        e.preventDefault();
        setQuery(e.target.value);
    }

    async function searchUniList(e) {
        e.preventDefault();
        var resp = await axios.get('http://api.openweathermap.org/data/2.5/weather', {params: { mode: 'json', APPID: '7e62df7908e6b6b3c2c08498d7bc8109', q: query}});
        console.log(resp)
    }

    return (
        <div>
            <input value={query} onChange={handleSearchQuery}/>
            <button onClick={searchUniList}>Search</button>
            <ul>
                <li>
                    {resp.name}
                </li>
                <li>
                    {resp.temp}
                </li>
            </ul>
        </div>
    )
}

export default Weather;
